Action.ExecScriptExpression:
using System;
using System.Linq;
using System.Text;
using Triggernometry;
using Triggernometry.Core;
using Triggernometry.FFXIV;
using Triggernometry.Utilities;

// 日志输出函数
static void Log(string msg) => RealPlugin.plug.InvokeNamedCallback("command", "/e " + msg);

// 变量赋值
var offset = ${v:The_Forked_Tower_Blood_level_offset};
var maxlevel = ${v:The_Forked_Tower_Blood_level_max};
var stars = ${v:The_Forked_Tower_Blood_level_stars};
var allEntities = Triggernometry.FFXIV.Entity.GetEntities().Where(e => e.BNpcID == 0);
int totalCount = allEntities.Count();

var output1Builder = new StringBuilder();
var output2Builder = new StringBuilder();

// 读内存和buff层数
foreach(var entity in allEntities)
{
    IntPtr finalAddress = (IntPtr)entity.Address + offset;
    Status status = entity.Statuses.FirstOrDefault(s => s.StatusID == 4226);
    var level = Memory.Read<byte>(Memory.XivProcHandle, finalAddress);
    
    if(level < maxlevel)
    {
        output1Builder.Append($"{entity.Name}({level}级),");
    }
    
    int stackCount = status?.Stack ?? 0;
    if(stackCount < stars)
    {
        output2Builder.Append($"{entity.Name}({stackCount}星),");
    }
}

// 处理输出结果
string output1 = output1Builder.Length == 0 
    ? "所有人都是满级" 
    : "没满级的是：" + output1Builder.ToString().TrimEnd(',');

string output2 = output2Builder.Length == 0 
    ? "所有人都是满星" 
    : "没满星的是：" + output2Builder.ToString().TrimEnd(',');

var output = $"附近人数：{totalCount}人\n{output1}\n{output2}";
Log(output);

using System;
using System.Linq;
using System.Text;
using System.Collections.Generic; // 添加此命名空间以使用Dictionary
using Triggernometry;
using Triggernometry.Core;
using Triggernometry.Utilities;
using Triggernometry.FFXIV;

// 日志输出函数
static void Log(string msg) => RealPlugin.plug.InvokeNamedCallback("command", "/e " + msg);

// 【4242=自由人，4358=骑士，4359=狂战士，4360=武僧，4361=猎人，4362=武士，4363=吟游诗人】
// 【4364=风水师，4365=时魔法师，4366=炮击士，4367=药剂师，4368=预言师，4369=盗贼】

// 使用StringBuilder提高字符串拼接性能
var output4242 = new StringBuilder();
var output4360 = new StringBuilder();
var output4368 = new StringBuilder();

// 计数器初始化
var counts = new Dictionary<int, int>
{
    {4358, 0}, {4359, 0}, {4361, 0}, {4362, 0},
    {4363, 0}, {4364, 0}, {4365, 0}, {4366, 0},
    {4367, 0}, {4369, 0}
};
int count4367TD = 0;

var allEntities = Triggernometry.FFXIV.Entity.GetEntities().Where(e => e.BNpcID == 0);
int totalCount = allEntities.Count();

// buff检测
foreach (var entity in allEntities)
{
    foreach (var status in entity.Statuses)
    {
        switch (status.StatusID)
        {
            case 4242:
                output4242.Append($"{entity.Name},");
                break;
            case 4360:
                output4360.Append($"{entity.Name},");
                break;
            case 4368:
                output4368.Append($"{entity.Name},");
                break;
            case 4367:
                counts[4367]++;
                if (!entity.Job.IsHealer)
                {
                    count4367TD++;
                }
                break;
            default:
                if (counts.ContainsKey(status.StatusID))
                {
                    counts[status.StatusID]++;
                }
                break;
        }
    }
}

// 处理输出结果
string GetOutput(StringBuilder sb, string noneMsg, string prefix)
{
    return sb.Length == 0 
        ? noneMsg 
        : $"{prefix}{sb.ToString().TrimEnd(',')}";
}

var output4242Str = GetOutput(output4242, "未发现辅助自由人", "辅助自由人是：");
var output4360Str = GetOutput(output4360, "未发现辅助武僧", "辅助武僧是：");
var output4368Str = GetOutput(output4368, "未发现辅助预言师", "辅助预言师是：");

var output = new StringBuilder()
    .AppendLine($"附近人数：{totalCount}人")
    .AppendLine(output4242Str)
    .AppendLine(output4360Str)
    .AppendLine(output4368Str)
    .AppendLine($"辅助盗贼{counts[4369]}人，辅助猎人{counts[4361]}人，辅助时魔法师{counts[4365]}人")
    .AppendLine($"辅助骑士{counts[4358]}人，辅助吟游诗人{counts[4363]}人，辅助武士{counts[4362]}人")
    .AppendLine($"辅助狂战士{counts[4359]}人,辅助风水师{counts[4364]}人，辅助炮击士{counts[4366]}人")
    .Append($"辅助药剂师{counts[4367]}人（火种{count4367TD}人）")
    .ToString();

Log(output);

using System;
using System.Text;
using System.Linq;
using System.Collections.Generic;
using Triggernometry;
using Triggernometry.Core;
using Triggernometry.Core.Variables;
using Triggernometry.Utilities;
using Triggernometry.FFXIV;

// 日志输出函数
static void Log(string msg) => RealPlugin.plug.InvokeNamedCallback("command", "/e " + msg);

// 标记实体的辅助方法
static void MarkEntity(Triggernometry.FFXIV.Entity entity, string markType)
{
    string markJson = $"{{\"ActorID\": 0x{entity.ID:X},\"MarkType\": \"{markType}\",\"LocalOnly\": false}}";
    RealPlugin.plug.InvokeNamedCallback("mark", markJson);
}

// 【buffID对应职业】
// 4242=自由人，4358=骑士，4359=狂战士，4360=武僧，4361=猎人
// 4362=武士，4363=吟游诗人，4364=风水师，4365=时魔法师
// 4366=炮击士，4367=药剂师，4368=预言师，4369=盗贼

// 使用StringBuilder优化字符串拼接
var output4242 = new StringBuilder();
var output4360 = new StringBuilder();
var output4368 = new StringBuilder();

// 使用int键字典初始化计数器（键=buffID，值=计数）
var counts = new Dictionary<int, int>
{
    {4358, 0}, {4359, 0}, {4361, 0}, {4362, 0},
    {4363, 0}, {4364, 0}, {4365, 0}, {4366, 0},
    {4367, 0}, {4369, 0}
};
// 药剂师火种单独计数（非buffID，单独定义）
int count4367TD = 0;

// 武士技能计数字典初始化（4个boss，每个包含"扔钱"和"居合"）
var allBossSkillDict = new VariableDictionary();
for (int i = 1; i <= 4; i++)
{
    var bossDict = new VariableDictionary();
    bossDict.SetValue("扔钱", new VariableDictionary());
    bossDict.SetValue("居合", new VariableDictionary());
    allBossSkillDict.SetValue($"boss{i}", bossDict);
}
var initializedSamurais = new VariableDictionary(); // 记录已初始化的武士

// 参考可运行代码，获取全局变量存储
VariableStore globalVarStore = RealPlugin.Instance.GetVariableStore(true);

// 获取附近玩家实体（排除NPC）
var allEntities = Triggernometry.FFXIV.Entity.GetEntities().Where(e => e.BNpcID == 0);
int totalCount = allEntities.Count();

// 检测buff并处理标记和字典初始化
foreach (Triggernometry.FFXIV.Entity entity in allEntities)
{
    if (entity == null || entity.Statuses == null) continue; // 空值防护
    
    foreach (var status in entity.Statuses)
    {
        if (status == null) continue;
        
        int statusId = status.StatusID;
        
        switch (statusId)
        {
            // 需记录名字的职业
            case 4242:
                output4242.Append(entity.Name).Append(",");
                break;
                
            case 4360:
                output4360.Append(entity.Name).Append(",");
                break;
                
            case 4368:
                output4368.Append(entity.Name).Append(",");
                break;
                
            // 仅需计数的职业（直接累加字典中的值）
            case 4358:
            case 4359:
            case 4363:
            case 4364:
            case 4366:
            case 4369:
                counts[statusId]++;
                break;
                
            // 猎人：计数+标记
            case 4361:
                counts[statusId]++;
                if (counts[statusId] < 3)
                {
                    MarkEntity(entity, $"stop{counts[statusId]}");
                }
                break;
                
            // 时魔法师：计数+标记
            case 4365:
                counts[statusId]++;
                if (counts[statusId] < 4)
                {
                    MarkEntity(entity, $"bind{counts[statusId]}");
                }
                break;
                
            // 药剂师：区分治疗/火种，分别计数+标记
            case 4367:
                if (entity.Job?.IsHealer ?? false) // 空值防护
                {
                    counts[statusId]++;
                    if (counts[statusId] < 9)
                    {
                        MarkEntity(entity, $"attack{counts[statusId]}");
                    }
                }
                else
                {
                    count4367TD++;
                    if (count4367TD < 2)
                    {
                        MarkEntity(entity, "cross");
                    }
                }
                break;
                
            // 武士：计数+技能字典初始化
            case 4362:
                counts[statusId]++;
                string samuraiKey = $"{entity.HexID}_{entity.Name}";
                if (!initializedSamurais.ContainsKey(samuraiKey))
                {
                    initializedSamurais.SetValue(samuraiKey, "0");
                    
                    // 为4个boss初始化该武士的技能计数
                    for (int bossIdx = 1; bossIdx <= 4; bossIdx++)
                    {
                        var bossDict = allBossSkillDict.GetValue($"boss{bossIdx}") as VariableDictionary;
                        var throwMoneyDict = bossDict.GetValue("扔钱") as VariableDictionary;
                        var iaijutsuDict = bossDict.GetValue("居合") as VariableDictionary;
                        
                        throwMoneyDict.SetValue(samuraiKey, "0");
                        iaijutsuDict.SetValue(samuraiKey, "0");
                    }
                }
                break;
        }
    }
}

// ====================== 最终修正：贴合 VariableStore 类定义的写入逻辑 ======================
// 1. 给武士技能总字典设置追踪属性（继承自Variable基类）
allBossSkillDict.LastChanger = "Mata";
allBossSkillDict.LastChanged = DateTime.Now;

// 2. 正确写入字典变量：使用 Dict 属性（而非 Dictionary），与标量 Scalar 用法一致
globalVarStore.Dict["The_Forked_Tower_Blood_all_boss_skills"] = allBossSkillDict;

// （可选：同理，写入 initializedSamurais 字典变量）
initializedSamurais.LastChanger = "Mata";
initializedSamurais.LastChanged = DateTime.Now;
globalVarStore.Dict["The_Forked_Tower_Blood_initializedSamurais"] = initializedSamurais;

// 处理输出结果（去除末尾逗号）
string output4242Str = output4242.Length == 0 
    ? "未发现辅助自由人" 
    : "辅助自由人是：" + output4242.ToString(0, output4242.Length - 1);

string output4360Str = output4360.Length == 0 
    ? "未发现辅助武僧" 
    : "辅助武僧是：" + output4360.ToString(0, output4360.Length - 1);

string output4368Str = output4368.Length == 0 
    ? "未发现辅助预言师" 
    : "辅助预言师是：" + output4368.ToString(0, output4368.Length - 1);

// 构建最终输出
var output = new StringBuilder()
    .AppendLine($"附近人数：{totalCount}人")
    .AppendLine(output4242Str)
    .AppendLine(output4360Str)
    .AppendLine(output4368Str)
    .AppendLine($"辅助盗贼{counts[4369]}人，辅助猎人{counts[4361]}人，辅助时魔法师{counts[4365]}人")
    .AppendLine($"辅助骑士{counts[4358]}人，辅助吟游诗人{counts[4363]}人，辅助武士{counts[4362]}人")
    .AppendLine($"辅助狂战士{counts[4359]}人,辅助风水师{counts[4364]}人，辅助炮击士{counts[4366]}人")
    .AppendLine($"辅助药剂师{counts[4367] + count4367TD}人（火种{count4367TD}人）")
    .Append($"已初始化{initializedSamurais.Values.Count}名武士的技能计数字典")  // 最后一行用Append
    .ToString();

Log(output);

using System;
using System.Linq;
using System.Text;
using System.Threading;
using Triggernometry;
using Triggernometry.Core;
using Triggernometry.FFXIV;

// 配置参数
const ushort TargetStatusID = 4354; // 目标buff ID
const double LoopInterval = 0; // 关键修改：将循环间隔改为0
const int MaxLoopCount = 30; // 最大循环次数

// 日志输出函数
static void Log(string message) => RealPlugin.plug.InvokeNamedCallback("command", $"/e [陨石检测] {message}");

// 获取当前Buff时间的辅助函数
static double? GetCurrentBuffTime()
{
    var buffEntity = Triggernometry.FFXIV.Entity.GetEntities()
        .Where(e => e.BNpcID == 0 
                 && e.Statuses != null 
                 && e.Statuses.Any(s => s.StatusID == TargetStatusID))
        .Select(e => e.Statuses.First(s => s.StatusID == TargetStatusID))
        .FirstOrDefault();
    
    return buffEntity?.Timer;
}

// 检测逻辑封装为独立方法（将在新线程中执行）
static void DetectionLogic()
{
    try
    {
        // 移除流程开始日志
        
        // 初始检测Buff是否存在
        double? currentTimer = GetCurrentBuffTime();
        if (!currentTimer.HasValue)
        {
            Log("未发现带有目标buff的实体，无检测结果"); // 仅保留核心结果
            return;
        }
        // 移除初始基准日志

        // 用于保存上一次有效结果
        string lastValidResult = "";
        int lastValidCount = 0;
        double? lastValidBuffTime = currentTimer;
        bool isBuffDisappeared = false;

        // 多线程中使用for循环执行检测
        for (int i = 0; i < MaxLoopCount; i++)
        {
            // 从第二次检测开始等待（非阻塞等待改为线程休眠）
            if (i > 0)
            {
                // 移除等待提示日志
                int waitMs = (int)Math.Round(LoopInterval * 1000); // 保留原有waitMs计算逻辑
                Thread.Sleep(waitMs); // 新线程中的休眠不会阻塞主线程
                // 移除等待完成日志
            }

            // 执行当前检测
            var entities = Triggernometry.FFXIV.Entity.GetEntities()
                .Where(e => e.BNpcID == 0)
                .ToList();

            var errorBuilder = new StringBuilder();
            int currentCount = 0;
            double? checkTimer = null;

            foreach (var entity in entities)
            {
                if (entity == null || entity.Statuses == null)
                    continue;

                var status = entity.Statuses.FirstOrDefault(s => s.StatusID == TargetStatusID);
                if (status != null)
                {
                    if (!checkTimer.HasValue)
                    {
                        checkTimer = status.Timer;
                    }
                    currentCount++;
                    if (Math.Abs(entity.PosY - ${y}) > 2)
                    {
                        errorBuilder.Append(entity.Name).Append(",");
                    }
                }
            }

            // 处理检测结果
            if (checkTimer.HasValue)
            {
                string currentResult = errorBuilder.Length == 0 
                    ? "没有人位置异常" 
                    : "位置异常的是：" + errorBuilder.ToString(0, errorBuilder.Length - 1);
                
                lastValidResult = currentResult;
                lastValidCount = currentCount;
                lastValidBuffTime = checkTimer;

                // 移除单次检测日志
                // 移除当前结果日志
            }
            else
            {
                // 移除当前检测无buff日志
                // 移除上一次结果临时输出日志
                isBuffDisappeared = true;
                break;
            }
        }

        // 循环结束处理
        if (!isBuffDisappeared)
        {
            // 移除达到最大次数提示日志
            Log($"最终检测结果：{lastValidResult}（共{lastValidCount}人带有目标buff）"); // 保留最终结果
        }
        else
        {
            Log($"最终检测结果：{lastValidResult}（共{lastValidCount}人带有目标buff，Buff已消失）"); // 保留Buff消失场景的最终结果
        }
        // 移除流程完成日志
    }
    catch (Exception ex)
    {
        Log($"检测过程发生错误：{ex.Message}"); // 保留错误日志
    }
}

// 主线程：创建并启动后台线程执行检测逻辑（非阻塞）
try
{
    // 创建新线程，指定执行检测逻辑的方法
    Thread detectionThread = new Thread(DetectionLogic);
    detectionThread.IsBackground = true; // 设置为后台线程，随主线程退出而退出
    detectionThread.Start(); // 启动线程（非阻塞，主线程继续执行）
    
    // 移除线程启动提示日志
}
catch (Exception ex)
{
    Log($"启动检测线程失败：{ex.Message}"); // 保留启动失败错误日志
}

using System;
using System.Linq;
using Triggernometry;
using Triggernometry.Core;
using Triggernometry.Core.Variables;
using Triggernometry.FFXIV;

// 日志输出函数
static void Log(string msg) => RealPlugin.plug.InvokeNamedCallback("command", "/e " + msg);

// 常量定义：与初始化逻辑保持一致
private const string GLOBAL_DICT_KEY = "The_Forked_Tower_Blood_all_boss_skills";
private const string TARGET_BOSS_KEY = "boss1";

// 关键修改：通过VariableStore获取全局变量（替代Interpreter.StaticHelpers）
VariableStore globalVarStore = RealPlugin.Instance.GetVariableStore(true); // true=全局变量

// 获取全局技能字典（与初始化时的字典结构匹配）
var allBossSkills = globalVarStore.GetDictVariable(GLOBAL_DICT_KEY); // 替代GetDictVariable(false, GLOBAL_DICT_KEY)
if (allBossSkills == null)
{
    Log("全局技能字典未初始化，无法重置");
    return;
}

// 获取boss4的子字典（对应初始化时的boss4层级，原逻辑不变）
var boss4Dict = allBossSkills.GetValue(TARGET_BOSS_KEY) as VariableDictionary;
if (boss4Dict == null)
{
    Log($"未找到{TARGET_BOSS_KEY}的字典，无法重置");
    return;
}

// 递归重置boss4字典中所有数值为0（包括嵌套的技能字典，原逻辑不变）
ResetNumericValues(boss4Dict);

// 关键修改：通过VariableStore保存更新后的全局字典（替代SetDictVariable）
globalVarStore.Dict[GLOBAL_DICT_KEY] = allBossSkills;
Log($"{TARGET_BOSS_KEY}字典中所有数值已重置为0");

// 递归处理方法：适配VariableDictionary结构（原逻辑不变）
static void ResetNumericValues(VariableDictionary dict)
{
    if (dict == null) return;
    
    // 遍历所有键（通过Values.Keys获取，符合VariableDictionary的结构）
    var keys = dict.Values.Keys.ToList();
    foreach (var key in keys)
    {
        var value = dict.GetValue(key);
        
        // 如果是子字典（如"扔钱"、"居合"技能的计数字典），递归处理
        if (value is VariableDictionary subDict)
        {
            ResetNumericValues(subDict);
        }
        // 如果是数值类型（初始化时存储为字符串形式的数字）
        else if (value is VariableScalar scalar)
        {
            // 验证是否为数字（与初始化时的存储方式匹配）
            if (int.TryParse(scalar.Value, out _) || 
                float.TryParse(scalar.Value, out _))
            {
                dict.SetValue(key, "0"); // 重置为0（保持字符串存储格式）
            }
        }
    }
}

using System;
using System.Linq;
using System.Text;
using Triggernometry;
using Triggernometry.Core;
using Triggernometry.Core.Variables;
using Triggernometry.Utilities;
using Triggernometry.FFXIV;

// 日志输出函数
static void Log(string msg) => RealPlugin.plug.InvokeNamedCallback("command", "/e " + msg);

// 使用StringBuilder优化字符串拼接性能
var output = new StringBuilder();
var allEntities = Triggernometry.FFXIV.Entity.GetEntities().Where(e => e.BNpcID == 0);

// 读取食物buff（状态ID=48）剩余时间，小于300秒告警
foreach (var entity in allEntities)
{
    // 空值防护：避免实体为空导致的异常
    if (entity == null || entity.Statuses == null) continue;
    
    Status foodStatus = entity.Statuses.FirstOrDefault(s => s.StatusID == 48);
    if (foodStatus != null)
    {
        // 剩余时间小于300秒（5分钟）时记录
        if (foodStatus.Timer < 300)
        {
            output.Append(entity.Name).Append("(").Append((int)foodStatus.Timer).Append("秒),");
        }
    }
    else
    {
        // 无食物buff时显示0秒
        output.Append(entity.Name).Append("(0秒),");
    }
}

// 处理输出结果
string result;
if (output.Length == 0)
{
    result = "所有人都吃饭了";
}
else
{
    // 移除末尾的逗号
    result = "食物时间不够的是：" + output.ToString(0, output.Length - 1);
}

Log(result);

