//int count = 0;
string output = "";
foreach(var entity in allEntities)
{
    if(entity.Statuses.Any(s => s.StatusID == 4354))
    {
        //count++;
        uint playerId = entity.ID;
        //string markJson = $"{{\"ActorID\": 0x{playerId:X},\"MarkType\": \"attack{count}\",\"LocalOnly\": true}}";
        //RealPlugin.plug.InvokeNamedCallback("mark", markJson);
        output = output + entity.Name + ",";
