}
output = output.TrimEnd(',');
output = "\n陨石在${var:The_Forked_Tower_Blood_boss1_comet}侧\n北侧分摊点：${lvar:The_Forked_Tower_Blood_boss1_comet_north[-1]}，${lvar:The_Forked_Tower_Blood_boss1_comet_north[-2]}\n南侧分摊点：${lvar:The_Forked_Tower_Blood_boss1_comet_south[-1]}，${lvar:The_Forked_Tower_Blood_boss1_comet_south[-2]}\n陨石点：" + output;
Log(output);

using System;
using System.Collections.Generic;
using System.Linq;
using Triggernometry;
using Triggernometry.Variables;
using Triggernometry.PluginBridges;
using Triggernometry.FFXIV;

// 获取全局武士技能字典（与初始化逻辑保持一致）
var allBossSkills = Interpreter.StaticHelpers.GetDictVariable(
    false, 
    "The_Forked_Tower_Blood_all_boss_skills"
) as VariableDictionary;

if (allBossSkills == null)
{
    return;
}

// 获取当前触发的技能相关参数
string aid = $"${aid}"; // 技能ID
string sid = $"${sid}"; // 玩家ID
int currentCount = 0;

// 定义技能与BOSS的映射关系（根据实际情况调整）
var skillBossMap = new Dictionary<string, (string bossKey, string skillName)>
{
    {"A286", ("boss1", "扔钱")},  // A286对应boss1的扔钱技能
    {"A285", ("boss1", "居合")}   // A285对应boss1的居合技能
};

// 检查当前技能是否在监控列表中
if (skillBossMap.TryGetValue(aid, out var skillInfo))
{
    // 获取对应BOSS的技能字典
    var bossDict = allBossSkills.GetValue(skillInfo.bossKey) as VariableDictionary;
    if (bossDict == null)
    {
        return;
    }

    // 获取对应技能的玩家计数字典
    var skillDict = bossDict.GetValue(skillInfo.skillName) as VariableDictionary;
    if (skillDict == null)
    {
        return;
    }

    // 查找匹配的玩家并更新计数（使用初始化时的唯一键格式）
    var targetKey = skillDict.Values
        .Select(v => v.Key)
        .FirstOrDefault(key => key.StartsWith(sid));

    if (!string.IsNullOrEmpty(targetKey))
    {
        // 解析当前计数并加1
        if (int.TryParse(skillDict.GetStringValue(targetKey), out currentCount))
        {
            skillDict.SetValue(targetKey, (currentCount + 1).ToString());
            
            // 保存更新后的字典
            bossDict.SetValue(skillInfo.skillName, skillDict);
            allBossSkills.SetValue(skillInfo.bossKey, bossDict);
            Interpreter.StaticHelpers.SetDictVariable(
                false, 
                "The_Forked_Tower_Blood_all_boss_skills", 
                allBossSkills
            );
        }
    }
}

using System;
using System.Linq;
using System.Text;
using Triggernometry;
using Triggernometry.Core;
using Triggernometry.Core.Variables;
using Triggernometry.FFXIV;

// 日志输出函数（与可运行代码保持一致）
static void Log(string msg) => RealPlugin.plug.InvokeNamedCallback("command", "/e " + msg);

// 常量定义：与初始化的全局字典保持一致
private const string GLOBAL_SKILL_DICT_KEY = "The_Forked_Tower_Blood_all_boss_skills";
private const string BOSS1_KEY = "boss1";
private const string SKILL_THROW_MONEY = "扔钱";
private const string SKILL_IAIJUTSU = "居合";

// 关键修改：参考可运行代码，通过VariableStore获取全局变量（替代Interpreter.StaticHelpers）
VariableStore globalVarStore = RealPlugin.Instance.GetVariableStore(true); // true=全局变量

// 获取全局技能字典及BOSS1的技能数据（替换原有Interpreter读取方式）
var allBossSkills = globalVarStore.GetDictVariable(GLOBAL_SKILL_DICT_KEY); // 替代GetDictVariable(false, GLOBAL_SKILL_DICT_KEY)
if (allBossSkills == null)
{
    return;
}

// 获取BOSS1的技能字典（遵循三层结构：全局→boss1→技能，原逻辑不变）
var boss1Dict = allBossSkills.GetValue(BOSS1_KEY) as VariableDictionary;
if (boss1Dict == null)
{
    return;
}

// 获取具体技能的计数字典（原逻辑不变）
var throwMoneyDict = boss1Dict.GetValue(SKILL_THROW_MONEY) as VariableDictionary;
var iaijutsuDict = boss1Dict.GetValue(SKILL_IAIJUTSU) as VariableDictionary;

if (throwMoneyDict == null || iaijutsuDict == null)
{
    return;
}

// 获取合格阈值（原逻辑不变）
int time1 = ${v:The_Forked_Tower_Blood_boss1_time1}; // 扔钱合格阈值
int time2 = ${v:The_Forked_Tower_Blood_boss1_time2}; // 居合合格阈值

// 使用StringBuilder优化字符串拼接（原逻辑不变）
var output1 = new StringBuilder();
var output2 = new StringBuilder();

// 检查扔钱技能合格情况（玩家键格式："HexID_名字"，原逻辑不变）
foreach (var item in throwMoneyDict.Values.OrderBy(kvp => kvp.Key))
{
    string playerKey = item.Key;
    // 从键中提取玩家名字（截取"HexID_"之后的部分）
    int nameStartIndex = playerKey.IndexOf('_') + 1;
    if (nameStartIndex <= 0 || nameStartIndex >= playerKey.Length)
        continue;
        
    string name = playerKey.Substring(nameStartIndex);
    
    // 解析计数并检查是否合格
    if (int.TryParse(item.Value?.ToString(), out int count) && count < time1)
    {
        output1.Append(name).Append($"({count}次),");
    }
}

// 检查居合技能合格情况（原逻辑不变）
foreach (var item in iaijutsuDict.Values.OrderBy(kvp => kvp.Key))
{
    string playerKey = item.Key;
    int nameStartIndex = playerKey.IndexOf('_') + 1;
    if (nameStartIndex <= 0 || nameStartIndex >= playerKey.Length)
        continue;
        
    string name = playerKey.Substring(nameStartIndex);
    
    if (int.TryParse(item.Value?.ToString(), out int count) && count < time2)
    {
        output2.Append(name).Append($"({count}次),");
    }
}

// 处理扔钱输出结果（原逻辑不变）
string result1 = output1.Length == 0 
    ? "boss1所有人扔钱都合格" 
    : "boss1扔钱不合格的是：" + output1.ToString(0, output1.Length - 1);

// 处理居合输出结果（原逻辑不变）
string result2 = output2.Length == 0 
    ? "boss1所有人居合都合格" 
    : "boss1居合不合格的是：" + output2.ToString(0, output2.Length - 1);

// 输出最终结果（原逻辑不变）
Log($"\n{result1}\n{result2}");

using System;
using System.Linq;
using Triggernometry;
using Triggernometry.Core;
using Triggernometry.Core.Variables;
using Triggernometry.FFXIV;

// 日志输出函数（与可运行模板保持一致，简化命名空间限定）
static void Log(string msg) => RealPlugin.plug.InvokeNamedCallback("command", "/e " + msg);

// 常量定义：与初始化逻辑保持一致
private const string GLOBAL_DICT_KEY = "The_Forked_Tower_Blood_all_boss_skills";
private const string TARGET_BOSS_KEY = "boss4";

// 关键修改：通过VariableStore获取全局变量（替代Interpreter.StaticHelpers）
VariableStore globalVarStore = RealPlugin.Instance.GetVariableStore(true); // true=全局变量

// 获取全局技能字典（与初始化时的字典结构匹配，替换原有Interpreter读取方式）
var allBossSkills = globalVarStore.GetDictVariable(GLOBAL_DICT_KEY);
if (allBossSkills == null)
{
    Log("全局技能字典未初始化，无法重置");
    return;
}

// 获取boss4的子字典（对应TARGET_BOSS_KEY，原逻辑不变）
var boss4Dict = allBossSkills.GetValue(TARGET_BOSS_KEY) as VariableDictionary;
if (boss4Dict == null)
{
    Log($"未找到{TARGET_BOSS_KEY}的字典，无法重置");
    return;
}

// 递归重置boss4字典中所有数值为0（包括嵌套的技能字典）
ResetNumericValues(boss4Dict);

// 关键修改：通过VariableStore保存更新后的全局字典（替代SetDictVariable）
globalVarStore.Dict[GLOBAL_DICT_KEY] = allBossSkills;
Log($"{TARGET_BOSS_KEY}字典中所有数值已重置为0");

// 递归处理方法：适配VariableDictionary结构（原逻辑不变）
static void ResetNumericValues(VariableDictionary dict)
{
    if (dict == null) return;
    
    // 遍历所有键（通过Values.Keys获取，符合VariableDictionary的结构）
    var keys = dict.Values.Keys.ToList();
    foreach (var key in keys)
    {
        var value = dict.GetValue(key);
        
        // 如果是子字典（如"扔钱"、"居合"技能的计数字典），递归处理
        if (value is VariableDictionary subDict)
        {
            ResetNumericValues(subDict);
        }
        // 如果是数值类型（初始化时存储为字符串形式的数字）
        else if (value is VariableScalar scalar)
        {
            // 验证是否为数字（与初始化时的存储方式匹配）
            if (int.TryParse(scalar.Value, out _) || 
                float.TryParse(scalar.Value, out _))
            {
                dict.SetValue(key, "0"); // 重置为0（保持字符串存储格式）
            }
        }
    }
}

using System;
using System.Linq;
using System.Text;
using Triggernometry;
using Triggernometry.Core;
using Triggernometry.Core.Variables;
using Triggernometry.Utilities;
using Triggernometry.FFXIV;

// 日志输出函数
static void Log(string msg) => RealPlugin.plug.InvokeNamedCallback("command", "/e " + msg);

// 使用StringBuilder优化字符串拼接性能
var output = new StringBuilder();
var allEntities = Triggernometry.FFXIV.Entity.GetEntities().Where(e => e.BNpcID == 0);

// 读取食物buff（状态ID=48）剩余时间，小于300秒告警
foreach (var entity in allEntities)
{
    // 空值防护：避免实体为空导致的异常
    if (entity == null || entity.Statuses == null) continue;
    
    Status foodStatus = entity.Statuses.FirstOrDefault(s => s.StatusID == 48);
    if (foodStatus != null)
    {
        // 剩余时间小于300秒（5分钟）时记录
        if (foodStatus.Timer < 300)
        {
            output.Append(entity.Name).Append("(").Append((int)foodStatus.Timer).Append("秒),");
        }
    }
    else
    {
        // 无食物buff时显示0秒
        output.Append(entity.Name).Append("(0秒),");
    }
}

// 处理输出结果
string result;
if (output.Length == 0)
{
    result = "所有人都吃饭了";
}
else
{
    // 移除末尾的逗号
    result = "食物时间不够的是：" + output.ToString(0, output.Length - 1);
}

Log(result);

