// 变量赋值
// 4336小圈4337大圈
var allEntities = Triggernometry.FFXIV.Entity.GetEntities().Where(e => e.BNpcID == 0);
var tmp = "";
var output1 = "";
var output2 = "";
var output3 = "";
var output4 = "";
foreach(var entity in allEntities)
{
    Status status4336 = entity.Statuses.FirstOrDefault(s => s.StatusID == 4336);
    Status status4337 = entity.Statuses.FirstOrDefault(s => s.StatusID == 4337);
    if(status4336 != null)
    {
        double dx1 = entity.PosX - 699.69;
        double dy1 = entity.PosY + 674.18;
        double radians1 = Math.Atan2(dx1, dy1);
        double dx2 = entity.PosX - 675.2;
        double dy2 = entity.PosY + 688.32;
        double radians2 = Math.Atan2(dx2, dy2);
        double dx3 = entity.PosX - 700;
        double dy3 = entity.PosY + 673.64;
        double radians3 = Math.Atan2(dx3, dy3);
        double dx4 = entity.PosX - 700;
        double dy4 = entity.PosY + 645.36;
        double radians4 = Math.Atan2(dx4, dy4);
        double dx5 = entity.PosX - 700.31;
        double dy5 = entity.PosY + 674.18;
        double radians5 = Math.Atan2(dx5, dy5);
        double dx6 = entity.PosX - 724.8;
        double dy6 = entity.PosY + 688.32;
        double radians6 = Math.Atan2(dx6, dy6);
        if(radians1 < -5 * Math.PI / 12 && radians1 > -11 * Math.PI / 12 && radians2 > Math.PI / 12 && radians2 < 7 * Math.PI / 12)
        {
            tmp = "左上";
        }
        else if(radians3 > -3 * Math.PI / 12 && radians3 < 3 * Math.PI / 12 && (radians4 < -9 * Math.PI / 12|| radians4 > 9 * Math.PI / 12))
        {
            tmp = "正下";
        }
        else if(radians5 > 5 * Math.PI / 12 && radians5 < 11 * Math.PI / 12 && radians6 < -1 * Math.PI / 12 && radians6 > -7 * Math.PI / 12)
        {
            tmp = "右上";
        }
        else
        {
            tmp = "错误";
        }
        if(status4336.Timer < 14)
        {
            output1 = output1 + entity.Name + "(" + tmp + "),";
        }
        else
        {
            output2 = output2 + entity.Name + "(" + tmp + "),";
        }
    }
    else if(status4337 != null)
    {
        double dx1 = entity.PosX - 699.69;
        double dy1 = entity.PosY + 674.18;
        double radians1 = Math.Atan2(dx1, dy1);
        double dx2 = entity.PosX - 675.2;
        double dy2 = entity.PosY + 688.32;
        double radians2 = Math.Atan2(dx2, dy2);
        double dx3 = entity.PosX - 700;
        double dy3 = entity.PosY + 673.64;
        double radians3 = Math.Atan2(dx3, dy3);
        double dx4 = entity.PosX - 700;
        double dy4 = entity.PosY + 645.36;
        double radians4 = Math.Atan2(dx4, dy4);
        double dx5 = entity.PosX - 700.31;
        double dy5 = entity.PosY + 674.18;
        double radians5 = Math.Atan2(dx5, dy5);
        double dx6 = entity.PosX - 724.8;
        double dy6 = entity.PosY + 688.32;
        double radians6 = Math.Atan2(dx6, dy6);
        if(radians1 < -5 * Math.PI / 12 && radians1 > -11 * Math.PI / 12 && radians2 > Math.PI / 12 && radians2 < 7 * Math.PI / 12)
        {
            tmp = "左上";
        }
        else if(radians3 > -3 * Math.PI / 12 && radians3 < 3 * Math.PI / 12 && (radians4 < -9 * Math.PI / 12|| radians4 > 9 * Math.PI / 12))
        {
            tmp = "正下";
        }
        else if(radians5 > 5 * Math.PI / 12 && radians5 < 11 * Math.PI / 12 && radians6 < -1 * Math.PI / 12 && radians6 > -7 * Math.PI / 12)
        {
            tmp = "右上";
        }
        else
        {
            tmp = "错误";
        }
        if(status4337.Timer < 14)
        {
            output3 = output3 + entity.Name + "(" + tmp + "),";
        }
        else
        {
            output4 = output4 + entity.Name + "(" + tmp + "),";
        }
    }
}
output1 = output1.TrimEnd(',');
output2 = output2.TrimEnd(',');
output3 = output3.TrimEnd(',');
output4 = output4.TrimEnd(',');
Log("\n" + "第一次大圈点：" + output3 + "\n" + "第一次小圈点：" + output1 + "\n" + "第二次大圈点：" + output4 + "\n" + "第二次小圈点：" + output2);

using System;
using System.Linq;
using Triggernometry;
using Triggernometry.Core; 
using Triggernometry.Utilities;
using Triggernometry.FFXIV;
// 日志输出函数
static void Log(string msg) => RealPlugin.plug.InvokeNamedCallback("command", "/e " + msg);
// 变量赋值
// 4336小圈4337大圈
var allEntities = Triggernometry.FFXIV.Entity.GetEntities().Where(e => e.BNpcID == 0);
var tmp = "";
var output1 = "";
var output2 = "";
var output3 = "";
foreach(var entity in allEntities)
{
    Status status = entity.Statuses.FirstOrDefault(s => s.StatusID == 4338);
    if(status != null)
    {
        double dx1 = entity.PosX - 699.69;
        double dy1 = entity.PosY + 674.18;
        double radians1 = Math.Atan2(dx1, dy1);
        double dx2 = entity.PosX - 675.2;
        double dy2 = entity.PosY + 688.32;
        double radians2 = Math.Atan2(dx2, dy2);
        double dx3 = entity.PosX - 700;
        double dy3 = entity.PosY + 673.64;
        double radians3 = Math.Atan2(dx3, dy3);
        double dx4 = entity.PosX - 700;
        double dy4 = entity.PosY + 645.36;
        double radians4 = Math.Atan2(dx4, dy4);
        double dx5 = entity.PosX - 700.31;
        double dy5 = entity.PosY + 674.18;
        double radians5 = Math.Atan2(dx5, dy5);
        double dx6 = entity.PosX - 724.8;
        double dy6 = entity.PosY + 688.32;
        double radians6 = Math.Atan2(dx6, dy6);
        if(radians1 < -5 * Math.PI / 12 && radians1 > -11 * Math.PI / 12 && radians2 > Math.PI / 12 && radians2 < 7 * Math.PI / 12)
        {
            tmp = "左上";
        }
        else if(radians3 > -3 * Math.PI / 12 && radians3 < 3 * Math.PI / 12 && (radians4 < -9 * Math.PI / 12|| radians4 > 9 * Math.PI / 12))
        {
            tmp = "正下";
        }
        else if(radians5 > 5 * Math.PI / 12 && radians5 < 11 * Math.PI / 12 && radians6 < -1 * Math.PI / 12 && radians6 > -7 * Math.PI / 12)
        {
            tmp = "右上";
        }
        else
        {
            tmp = "错误";
        }
        if(status.Timer < 18)
        {
            output1 = output1 + entity.Name + "(" + tmp + "),";
        }
        else if(status.Timer < 26)
        {
            output2 = output2 + entity.Name + "(" + tmp + "),";
        }
        else if(status.Timer < 33)
        {
            output3 = output3 + entity.Name + "(" + tmp + "),";
        }
    }
}
output1 = output1.TrimEnd(',');
output2 = output2.TrimEnd(',');
output3 = output3.TrimEnd(',');
Log("\n" + "第一轮分摊点：" + output1 + "\n" + "第二轮分摊点：" + output2 + "\n" + "第三轮分摊点：" + output3 );

using System;
using System.Linq;
using Triggernometry;
using Triggernometry.Core;
using Triggernometry.Core.Variables; // 替换为正确的核心变量命名空间
using Triggernometry.FFXIV;

// 日志输出函数（与可运行模板保持一致）
static void Log(string msg) => RealPlugin.plug.InvokeNamedCallback("command", "/e " + msg);

// 常量定义：与初始化逻辑保持一致
private const string GLOBAL_DICT_KEY = "The_Forked_Tower_Blood_all_boss_skills";
private const string TARGET_BOSS_KEY = "boss2";

// 关键修改：通过VariableStore获取全局变量（替代Interpreter.StaticHelpers）
VariableStore globalVarStore = RealPlugin.Instance.GetVariableStore(true); // true=全局变量

// 获取全局技能字典（与初始化时的字典结构匹配，替换原有Interpreter读取方式）
var allBossSkills = globalVarStore.GetDictVariable(GLOBAL_DICT_KEY); // 替代GetDictVariable(false, GLOBAL_DICT_KEY)
if (allBossSkills == null)
{
    Log("全局技能字典未初始化，无法重置");
    return;
}

// 获取boss2的子字典（修正变量名，对应TARGET_BOSS_KEY，原逻辑不变）
var boss2Dict = allBossSkills.GetValue(TARGET_BOSS_KEY) as VariableDictionary; // 原boss4Dict改为boss2Dict，语义更准确
if (boss2Dict == null)
{
    Log($"未找到{TARGET_BOSS_KEY}的字典，无法重置");
    return;
}

// 递归重置boss2字典中所有数值为0（包括嵌套的技能字典，原逻辑不变）
ResetNumericValues(boss2Dict);

// 关键修改：通过VariableStore保存更新后的全局字典（替代SetDictVariable）
globalVarStore.Dict[GLOBAL_DICT_KEY] = allBossSkills;
Log($"{TARGET_BOSS_KEY}字典中所有数值已重置为0");

// 递归处理方法：适配VariableDictionary结构（原逻辑不变）
static void ResetNumericValues(VariableDictionary dict)
{
    if (dict == null) return;
    
    // 遍历所有键（通过Values.Keys获取，符合VariableDictionary的结构）
    var keys = dict.Values.Keys.ToList();
    foreach (var key in keys)
    {
        var value = dict.GetValue(key);
        
        // 如果是子字典（如"扔钱"、"居合"技能的计数字典），递归处理
        if (value is VariableDictionary subDict)
        {
            ResetNumericValues(subDict);
        }
        // 如果是数值类型（初始化时存储为字符串形式的数字）
        else if (value is VariableScalar scalar)
        {
            // 验证是否为数字（与初始化时的存储方式匹配）
            if (int.TryParse(scalar.Value, out _) || 
                float.TryParse(scalar.Value, out _))
            {
                dict.SetValue(key, "0"); // 重置为0（保持字符串存储格式）
            }
        }
    }
}

using System;
using System.Linq;
using System.Text;
using Triggernometry;
using Triggernometry.Core;
using Triggernometry.Core.Variables;
using Triggernometry.Utilities;
using Triggernometry.FFXIV;

// 日志输出函数
static void Log(string msg) => RealPlugin.plug.InvokeNamedCallback("command", "/e " + msg);

// 使用StringBuilder优化字符串拼接性能
var output = new StringBuilder();
var allEntities = Triggernometry.FFXIV.Entity.GetEntities().Where(e => e.BNpcID == 0);

// 读取食物buff（状态ID=48）剩余时间，小于300秒告警
foreach (var entity in allEntities)
{
    // 空值防护：避免实体为空导致的异常
    if (entity == null || entity.Statuses == null) continue;
    
    Status foodStatus = entity.Statuses.FirstOrDefault(s => s.StatusID == 48);
    if (foodStatus != null)
    {
        // 剩余时间小于300秒（5分钟）时记录
        if (foodStatus.Timer < 300)
        {
            output.Append(entity.Name).Append("(").Append((int)foodStatus.Timer).Append("秒),");
        }
    }
    else
    {
        // 无食物buff时显示0秒
        output.Append(entity.Name).Append("(0秒),");
    }
}

// 处理输出结果
string result;
if (output.Length == 0)
{
    result = "所有人都吃饭了";
}
else
{
    // 移除末尾的逗号
    result = "食物时间不够的是：" + output.ToString(0, output.Length - 1);
}

Log(result);

using System;
using System.Linq;
using System.Text;
using Triggernometry;
using Triggernometry.Core;
using Triggernometry.Core.Variables;
using Triggernometry.FFXIV;

// 日志输出函数（与可运行模板保持一致）
static void Log(string msg) => RealPlugin.plug.InvokeNamedCallback("command", "/e " + msg);

// 常量定义：保持优化代码的常量结构
private const string GLOBAL_SKILL_DICT_KEY = "The_Forked_Tower_Blood_all_boss_skills";
private const string TARGET_BOSS_KEY = "boss2";  // 仅修改为boss2
private const string SKILL_THROW_MONEY = "扔钱";
private const string SKILL_IAIJUTSU = "居合";

// 关键修改：通过VariableStore获取全局变量（替代Interpreter.StaticHelpers）
VariableStore globalVarStore = RealPlugin.Instance.GetVariableStore(true); // true=全局变量

// 获取全局技能字典及目标BOSS的技能数据（替换原有Interpreter读取方式）
var allBossSkills = globalVarStore.GetDictVariable(GLOBAL_SKILL_DICT_KEY);
if (allBossSkills == null)
{
    Log("全局技能字典未初始化");
    return;
}

// 获取目标BOSS的技能字典
var targetBossDict = allBossSkills.GetValue(TARGET_BOSS_KEY) as VariableDictionary;
if (targetBossDict == null)
{
    Log($"未找到{TARGET_BOSS_KEY}的技能字典");  // 动态使用常量
    return;
}

// 获取具体技能的计数字典
var throwMoneyDict = targetBossDict.GetValue(SKILL_THROW_MONEY) as VariableDictionary;
var iaijutsuDict = targetBossDict.GetValue(SKILL_IAIJUTSU) as VariableDictionary;

if (throwMoneyDict == null || iaijutsuDict == null)
{
    Log("技能计数字典获取失败");
    return;
}

// 获取合格阈值（使用目标BOSS的阈值变量）
int time1 = ${v:The_Forked_Tower_Blood_boss2_time1};  // 仅修改为boss2
int time2 = ${v:The_Forked_Tower_Blood_boss2_time2};  // 仅修改为boss2

// 使用StringBuilder优化字符串拼接（保留优化特性）
var output1 = new StringBuilder();
var output2 = new StringBuilder();

// 检查扔钱技能合格情况
foreach (var item in throwMoneyDict.Values.OrderBy(kvp => kvp.Key))
{
    string playerKey = item.Key;
    // 动态提取玩家名字（保留优化的提取逻辑）
    int nameStartIndex = playerKey.IndexOf('_') + 1;
    if (nameStartIndex <= 0 || nameStartIndex >= playerKey.Length)
        continue;
        
    string name = playerKey.Substring(nameStartIndex);
    
    if (int.TryParse(item.Value?.ToString(), out int count) && count < time1)
    {
        output1.Append(name).Append($"({count}次),");
    }
}

// 检查居合技能合格情况
foreach (var item in iaijutsuDict.Values.OrderBy(kvp => kvp.Key))
{
    string playerKey = item.Key;
    int nameStartIndex = playerKey.IndexOf('_') + 1;
    if (nameStartIndex <= 0 || nameStartIndex >= playerKey.Length)
        continue;
        
    string name = playerKey.Substring(nameStartIndex);
    
    if (int.TryParse(item.Value?.ToString(), out int count) && count < time2)
    {
        output2.Append(name).Append($"({count}次),");
    }
}

// 处理输出结果（使用常量确保BOSS名称一致）
string result1 = output1.Length == 0 
    ? $"{TARGET_BOSS_KEY}所有人扔钱都合格" 
    : $"{TARGET_BOSS_KEY}扔钱不合格的是：" + output1.ToString(0, output1.Length - 1);

string result2 = output2.Length == 0 
    ? $"{TARGET_BOSS_KEY}所有人居合都合格" 
    : $"{TARGET_BOSS_KEY}居合不合格的是：" + output2.ToString(0, output2.Length - 1);

// 输出最终结果
Log($"\n{result1}\n{result2}");

using System;
using System.Collections.Generic;
using System.Linq;
using Triggernometry;
using Triggernometry.Core;
using Triggernometry.Core.Variables;
using Triggernometry.FFXIV;

// 关键修改：通过VariableStore获取全局变量（替代Interpreter.StaticHelpers）
VariableStore globalVarStore = RealPlugin.Instance.GetVariableStore(true); // true=全局变量

// 获取全局武士技能字典（与初始化逻辑保持一致，替换原有Interpreter读取方式）
var allBossSkills = globalVarStore.GetDictVariable("The_Forked_Tower_Blood_all_boss_skills");

if (allBossSkills == null)
{
    return;
}

// 获取当前触发的技能相关参数
string aid = $"${aid}"; // 技能ID
string sid = $"${sid}"; // 玩家ID
int currentCount = 0;

// 定义技能与BOSS的映射关系（根据实际情况调整）
var skillBossMap = new Dictionary<string, (string bossKey, string skillName)>
{
    {"A286", ("boss2", "扔钱")},  // A286对应boss1的扔钱技能
    {"A285", ("boss2", "居合")}   // A285对应boss1的居合技能
};

// 检查当前技能是否在监控列表中
if (skillBossMap.TryGetValue(aid, out var skillInfo))
{
    // 获取对应BOSS的技能字典
    var bossDict = allBossSkills.GetValue(skillInfo.bossKey) as VariableDictionary;
    if (bossDict == null)
    {
        return;
    }

    // 获取对应技能的玩家计数字典
    var skillDict = bossDict.GetValue(skillInfo.skillName) as VariableDictionary;
    if (skillDict == null)
    {
        return;
    }

    // 查找匹配的玩家并更新计数（使用初始化时的唯一键格式）
    var targetKey = skillDict.Values
        .Select(v => v.Key)
        .FirstOrDefault(key => key.StartsWith(sid));

    if (!string.IsNullOrEmpty(targetKey))
    {
        // 解析当前计数并加1
        if (int.TryParse(skillDict.GetStringValue(targetKey), out currentCount))
        {
            skillDict.SetValue(targetKey, (currentCount + 1).ToString());
            
            // 保存更新后的字典（替换Interpreter写入方式）
            bossDict.SetValue(skillInfo.skillName, skillDict);
            allBossSkills.SetValue(skillInfo.bossKey, bossDict);
            globalVarStore.Dict["The_Forked_Tower_Blood_all_boss_skills"] = allBossSkills;
        }
    }
}

using System;
