Action.ExecScriptExpression:
using System;
using System.Threading;
using System.Collections.Generic;
using Triggernometry;
using Triggernometry.Core;
using Triggernometry.Core.Variables;
//using Triggernometry.Core.Scripting;

// 1. 从触发器变量 v:guan 中获取原始坐标数据
string rawData = $"${v:guan}";

// 2. 将原始数据按分号分隔，解析为坐标字符串数组
string[] coordArray = rawData.Split(';');

// 3. 特殊情况处理：如果只有一组有效坐标，无需处理直接终止
if (coordArray.Length == 1 && !string.IsNullOrEmpty(coordArray[0]))
{
    return;
}

// 4. 获取筛选参数值
string targetDirection = $"${param1}";  // 获取目标方向编码（如"02"）
string targetDistanceCode = $"${id}";    // 获取目标距离编码

// 5. 清除历史标记
string json = "{\"A\": {}, \"B\": {}, \"C\": {}, \"D\": {}, \"One\": {}, \"Two\": {}, \"Three\": {}, \"Four\": {}}";
RealPlugin.plug.InvokeNamedCallback("place", json);

// 6. 根据目标距离编码确定对应的距离范围（增加10单位容错，保持中点不变）
float minDistance = 0;
float maxDistance = float.MaxValue;

switch (targetDistanceCode)
{
    case "2AED":
        // 原范围: >200，中点无上限，容错后: >190
        minDistance = 190;
        maxDistance = float.MaxValue;
        break;
    case "2AEC":
        // 原范围: 100-200，中点150，容错后: 90-210
        minDistance = 90;
        maxDistance = 210;
        break;
    case "2AEB":
        // 原范围: 25-100，中点62.5，容错后: 15-110
        minDistance = 15;
        maxDistance = 110;
        break;
    case "2AEA":
        // 原范围: 5-25，中点15，容错后: 0-35（0为下限）
        minDistance = 0;
        maxDistance = 35;
        break;
    default:
        // 未知距离编码，不进行筛选
        return;
}

// 7. 筛选符合条件的坐标并存储结果
string filteredCoordString = "";
string place = "A";

foreach (var coordStr in coordArray)
{
    var parts = coordStr.Split(',');
    if (parts.Length != 3) 
        continue;

    if (!float.TryParse(parts[0], out float x) || 
        !float.TryParse(parts[1], out float y) || 
        !float.TryParse(parts[2], out float z))
        continue;

    // 计算相对位置和距离
    float dx = x - (float)${v:guanPosX};
    float dy = (float)${v:guanPosY} - y;
    float distance = (float)Math.Sqrt(dx * dx + dy * dy);

    // 计算方向角度
    double radians = Math.Atan2(dx, dy);
    double degrees = radians * (180 / Math.PI);
    if (degrees < 0)
        degrees += 360;

    // 检查是否在目标方向范围内（每个方向54度，保持原始中点）
    bool isInDirectionRange = false;
    switch (targetDirection)
    {
        case "01":
            // 01方向：中点11.25度，范围11.25±27度 → 334.25°-360° 和 0°-38.25°
            isInDirectionRange = (degrees >= 334.25 && degrees <= 360) || 
                                 (degrees >= 0 && degrees <= 38.25);
            break;
        case "02":
            // 02方向：中点45度，范围45±27度 → 18°-72°
            isInDirectionRange = degrees >= 18 && degrees <= 72;
            break;
        case "03":
            // 03方向：中点90度，范围90±27度 → 63°-117°
            isInDirectionRange = degrees >= 63 && degrees <= 117;
            break;
        case "04":
            // 04方向：中点135度，范围135±27度 → 108°-162°
            isInDirectionRange = degrees >= 108 && degrees <= 162;
            break;
        case "05":
            // 05方向：中点180度，范围180±27度 → 153°-207°
            isInDirectionRange = degrees >= 153 && degrees <= 207;
            break;
        case "06":
            // 06方向：中点225度，范围225±27度 → 198°-252°
            isInDirectionRange = degrees >= 198 && degrees <= 252;
            break;
        case "07":
            // 07方向：中点270度，范围270±27度 → 243°-297°
            isInDirectionRange = degrees >= 243 && degrees <= 297;
            break;
        case "08":
            // 08方向：中点315度，范围315±27度 → 288°-342°
            isInDirectionRange = degrees >= 288 && degrees <= 342;
            break;
        default:
            // 未知方向编码，不进行筛选
            isInDirectionRange = false;
            break;
    }

    // 检查是否在目标距离范围内（包含容错）
    bool isInDistanceRange = distance > minDistance && distance <= maxDistance;

    // 筛选符合条件的坐标
    if (isInDirectionRange && isInDistanceRange)
    {
        if (string.IsNullOrEmpty(filteredCoordString))
        {
            filteredCoordString = coordStr;
        }
        else
        {
            filteredCoordString += ";" + coordStr;
        }
        
        // 在地图上标记当前坐标
        string markJson = $"{{\"{place}\": {{\"X\": {x}, \"Z\": {y}, \"Y\": {z}, \"Active\": true}}}}";
        RealPlugin.plug.InvokeNamedCallback("place", markJson);

        // 更新下一个标记位
        string[] placeOrder = { "A", "B", "C", "D", "One", "Two", "Three", "Four" };
        int currentIndex = Array.IndexOf(placeOrder, place);
        
        if (currentIndex >= 0 && currentIndex < placeOrder.Length - 1)
        {
            place = placeOrder[currentIndex + 1];
        }
    }
}

// 8. 更新变量
if (!string.IsNullOrEmpty(filteredCoordString))
{
    VariableScalar guanVar = new VariableScalar();
    guanVar.Value = filteredCoordString;
    guanVar.LastChanger = "Mata"; 
    guanVar.LastChanged = DateTime.Now;

    VariableStore targetVarStore = RealPlugin.Instance.GetVariableStore(false);
    targetVarStore.Scalar["guan"] = guanVar;
}
